// src/components/SubmitTab.js
import React, { useState } from 'react';
import { useEssays } from '../context/EssayContext';

function SubmitTab() {
  const [essay, setEssay] = useState('');
  const [authorName, setAuthorName] = useState('');
  const { addEssay } = useEssays();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!essay.trim()) {
      alert('Essay content cannot be empty!');
      return;
    }

    addEssay({
      content: essay,
      isAnonymous: !authorName.trim(),
      authorName: authorName.trim() || 'Anonymous',
    });

    setEssay('');
    setAuthorName('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <textarea
        value={essay}
        onChange={(e) => setEssay(e.target.value)}
        placeholder="Write your essay here"
      />
      <div className="input-wrapper">
        <input
          type="text"
          value={authorName}
          onChange={(e) => setAuthorName(e.target.value)}
          className="author-name-input"
          aria-label="Author Name"
          autoComplete="off"
        />
        <span
          className={`placeholder-text ${authorName ? 'hidden' : ''}`}
        >
          Author Name (<i>Leave blank if you want to remain anonymous</i>)
        </span>
      </div>
      <button className="subessay-button" type="submit">
        Submit Essay
      </button>
    </form>
  );
}

export default SubmitTab;
