import React, { useState, useEffect } from 'react';

function ReadTab() {
  const [essays, setEssays] = useState([]);
  const [sortBy, setSortBy] = useState('mostLiked');

  useEffect(() => {
    // TODO: Implement API call to fetch essays
    console.log('Fetching essays, sorted by:', sortBy);
  }, [sortBy]);

  const handleLike = async (essayId) => {
    // TODO: Implement API call to like essay
    console.log('Liking essay:', essayId);
  };

  return (
    <div>
      <select onChange={(e) => setSortBy(e.target.value)}>
        <option value="mostLiked">Most Liked</option>
        <option value="mostRecent">Most Recent</option>
      </select>
      {essays.map((essay) => (
        <div key={essay.id}>
          <p>{essay.content}</p>
          <p>By: {essay.isAnonymous ? 'Anonymous' : essay.authorName}</p>
          <button onClick={() => handleLike(essay.id)}>
            Like ({essay.likes})
          </button>
        </div>
      ))}
    </div>
  );
}

export default ReadTab;
