import React, { useState } from 'react';
import { EssayProvider } from './EssayContext'; // Assuming you have this context set up
import SubmitTab from './SubmitTab'; // Import your SubmitTab component
import ReadTab from './ReadTab'; // Import your ReadTab component

function App() {
  const [activeTab, setActiveTab] = useState('submit');

  return (
    <EssayProvider>
      <div className="App">
        <nav className="container">
          <button
            className={`button ${activeTab === 'submit' ? 'active' : ''}`}
            onClick={() => setActiveTab('submit')}
          >
            Submit
          </button>
          <button
            className={`button ${activeTab === 'read' ? 'active' : ''}`}
            onClick={() => setActiveTab('read')}
          >
            Read
          </button>
        </nav>
        {activeTab === 'submit' ? <SubmitTab /> : <ReadTab />}
      </div>
    </EssayProvider>
  );
}

export default App;
