// src/components/SubmitTab.js
import React, { useState } from 'react';
import { useEssays } from '../context/EssayContext';

function SubmitTab() {
  const [essay, setEssay] = useState('');
  const [authorName, setAuthorName] = useState(''); // Remove isAnonymous state
  const { addEssay } = useEssays();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validation: Check if the essay content is not empty
    if (!essay.trim()) {
      alert('Essay content cannot be empty!');
      return;
    }

    // Add the essay
    addEssay({
      content: essay,
      isAnonymous: !authorName.trim(), // Automatically determine anonymity based on authorName
      authorName: authorName.trim() || 'Anonymous', // Set author name based on input
    });

    // Reset form fields
    setEssay('');
    setAuthorName('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <textarea 
        value={essay} 
        onChange={(e) => setEssay(e.target.value)}
        placeholder="Write your essay here"
      />
      <input
        type="text"
        value={authorName}
        onChange={(e) => setAuthorName(e.target.value)}
        placeholder="Leave blank if you want to remain anonymous"
        className="author-name-input"
        style={{ color: authorName ? 'black' : 'gray' }} // Style placeholder text
      />
      <button className="subessay-button" type="submit">Submit Essay</button>
    </form>
  );
}

export default SubmitTab;
